package javaprograms;

public class EvenOrOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

for(int i=2;i<=10;i+=2)
{
	System.out.println("even number "+i);
 }
for(int i=1;i<=10;i++)
{
	if(i%2!=0)
	{
	System.out.println("odd number"+i);
}
	}
}
}

